import React, { useState } from "react";
import useFetchData from "../hooks/useFetchData";
import Skeleton from "react-loading-skeleton";
import "../App.css"; // Import the CSS file

const SelectionView = () => {
    const [formData, setFormData] = useState({
        question: "",
        folder: "",
        numDocs: "",
    });
    const [response, setResponse] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);
    const [reload, setReload] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const {data, isLoading, isError, reload} = useFetchData(formData);
            setResponse(data);
            setIsLoading(isLoading);
            setIsError(isError);
            setReload(reload);
        } catch (error) {
            console.error("Error fetching data: ", error);
            setIsError(true);
        }
    };

    return (
        <div className="container">
            <div>
                <form onSubmit={handleSubmit}>
                    <div className="input-section">
                        <div className="input-field">
                            <label htmlFor="question">Question</label>
                            <input
                                id="question"
                                type="text"
                                name="question"
                                value={formData.question}
                                placeholder="Enter your question"
                                onChange={handleChange}
                                className="custom-input"
                            />
                        </div>

                        <div className="input-field">
                            <label htmlFor="folder">Select Client folder</label>
                            <select
                                id="folder"
                                name="folder"
                                value={formData.folder}
                                onChange={handleChange}
                                className="custom-select"
                            >
                                <option value="default-folder">
                                    Default folder
                                </option>
                            </select>
                        </div>

                        <div className="input-field">
                            <label htmlFor="numDocs">
                                Select Number of documents
                            </label>
                            <select
                                id="numDocs"
                                name="numDocs"
                                value={formData.numDocs}
                                onChange={handleChange}
                                className="custom-select"
                            >
                                {[...Array(10)].map((_, i) => (
                                    <option key={i + 1} value={i + 1}>
                                        {i + 1}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>
                    <div className="submit-section">
                        <button className="submit-button" type="submit">
                            Submit
                        </button>
                    </div>
                </form>
            </div>

            {
                isLoading ? (
                    <div className="loading-skeleton">
                        <Skeleton count={3} />
                    </div>
                ) : isError ? (
                    <div className="error-message">
                        <p>There was an error processing your request.</p>
                        <button onClick={reload}>Try again</button>
                    </div>
                ) : response ? (
                    <div className="success-message">
                        <p>Request processed successfully.</p>
                    </div>
                ) : (
                    <div className="initial-message">
                        <p>
                            Please enter your question and select the client
                            folder and number of documents to retrieve the
                            documents and the GPT answer.
                        </p>
                    </div>
                )
            }

            {response && (
                <div className="response-section">
                    <div className="doc-response">
                        <h3>
                            <u>Docs retrieved:</u>
                        </h3>
                        {isError ? (
                            <p>There was an error retrieving the documents.</p>
                        ) : isLoading ? (
                            <p>Loading documents...</p>
                        ) : response.docs.length === 0 ? (
                            <p>No documents found for the selected folder.</p>
                        ) : (
                            response.docs.map((doc) => (
                                <div className="doc">
                                    <h2>{doc.snippet}</h2>
                                    <div key={doc.id} className="doc-info">
                                        {doc.filePath && (
                                            <h4>File path: {doc.filePath}</h4>
                                        )}
                                        {doc.batesNumber && (
                                            <h4>
                                                Bates number: {doc.batesNumber}
                                            </h4>
                                        )}
                                        {doc.pageNumber && (
                                            <h4>
                                                Page number: {doc.pageNumber}
                                            </h4>
                                        )}
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                    <div className="answer-response">
                        <h3>
                            <u>GPT Answer:</u>
                        </h3>
                        <p>{response.gptAnswer}</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default SelectionView;
