import React, { useState } from "react";
import "../App.css"; // Import the CSS file

const SelectionView = () => {
    const [formData, setFormData] = useState({
        question: "",
        folder: "",
        numDocs: "",
    });
    const [response, setResponse] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const apiResponse = await fetch(
                "https://example.com/api/submit-form",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(formData),
                }
            );

            const result = await apiResponse.json();
            setResponse(result);
        } catch (error) {
            console.error("Error submitting form:", error);
        }
    };

    return (
        <div className="container">
            <div>
                <form onSubmit={handleSubmit} className="input-section">
                    <div className="input-field">
                        <label htmlFor="question">Question</label>
                        <input
                            id="question"
                            type="text"
                            name="question"
                            value={formData.question}
                            placeholder="Enter your question"
                            onChange={handleChange}
                            className="custom-input"
                        />
                    </div>

                    <div className="input-field">
                        <label htmlFor="folder">Folder</label>
                        <select
                            id="folder"
                            name="folder"
                            value={formData.folder}
                            onChange={handleChange}
                            className="custom-select" // Apply custom class
                        >
                            <option value="">Select Client folder</option>
                            <option value="default-folder">
                                Default folder
                            </option>
                            {/* Add more options as needed */}
                        </select>
                    </div>

                    <div className="input-field">
                        <label htmlFor="numDocs">
                            Select Number of documents
                        </label>
                        <select
                            id="numDocs"
                            name="numDocs"
                            value={formData.numDocs}
                            onChange={handleChange}
                            className="custom-select" // Apply custom class
                        >
                            {[...Array(10)].map((_, i) => (
                                <option key={i + 1} value={i + 1}>
                                    {i + 1}
                                </option>
                            ))}
                        </select>
                    </div>

                    <button className="submit-button" type="submit">
                        Submit
                    </button>
                </form>
            </div>

            <div className="response-section">
                {response && (
                    <div>
                        <h3>Response:</h3>
                        <p>{JSON.stringify(response)}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SelectionView;
