import React, { useState } from "react";

const dummyData = [
    {
        id: 1,
        question: "What is the capital of France?",
        answer: "Paris",
        folder: "Geography",
    },
    // ... other data
];

export const SelectionView = () => {
    const [formData, setFormData] = useState({
        question: "",
        folder: "",
        numDocs: "",
    });
    const [response, setResponse] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const apiResponse = await fetch(
                "https://example.com/api/submit-form",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(formData),
                }
            );

            const result = await apiResponse.json();
            setResponse(result);
        } catch (error) {
            console.error("Error submitting form:", error);
        }
    };

    return (
        <div>
            <div className="input-section">
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="question"
                        value={formData.question}
                        placeholder="Question Input"
                        onChange={handleChange}
                    />
                    <select
                        name="folder"
                        value={formData.folder}
                        onChange={handleChange}
                    >
                        <option value="">Client Selection Dropdown</option>
                        <option value="default-folder">Default folder</option>
                    </select>
                    <select
                        name="numDocs"
                        value={formData.numDocs}
                        onChange={handleChange}
                    >
                        <option value=""> Number of documents</option>
                        {[...Array(10)].map((_, i) => (
                            <option key={i + 1} value={i + 1}>
                                {i + 1}
                            </option>
                        ))}
                    </select>
                </form>
            </div>
            <div className="submit-section">
                <button className="submit-button" type="submit">
                    Submit
                </button>
            </div>
            <div className="response-section">
                {response && (
                    <div>
                        <h3>Response:</h3>
                        <p>{JSON.stringify(response)}</p>
                    </div>
                )}
            </div>
        </div>
    );
};
